}
","keccak256":"0xf9733c6760338dc2f25c3fa03704664982a537007a07e930691919e318931960","license":"MIT"},"contracts/interfaces/ISuperSoulbound.sol":{"content":"// SPDX-License-Identifier: AGPLv3
pragma solidity 0.8.16;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title Superfluid compatible super soulbound token interface
 *
 * @author 0xPilou
 */
interface ISuperSoulbound {
  function initialize(
    IERC20 underlyingToken,
    uint8 underlyingDecimals,
    string calldata n,
    string calldata s
  ) external ;

  function proxiableUUID() external pure  returns (bytes32);

  function updateCode(address newAddress) external ;

  /**************************************************************************
   * ERC20 Token Info
   *************************************************************************/

  function name() external view  returns (string memory);

  function symbol() external view  returns (string memory);

  function decimals() external pure  returns (uint8);

  /**************************************************************************
   * ERC20 Implementations
   *************************************************************************/

  function totalSupply() external view  returns (uint256);

  function balanceOf(address account)
    external
    view

    returns (uint256 balance);

  function transfer(address recipient, uint256 amount)
    external

    returns (bool);

  function allowance(address account, address spender)
    external
    view

    returns (uint256);

  function approve(address spender, uint256 amount)
    external

    returns (bool);

  function transferFrom(
    address holder,
    address recipient,
    uint256 amount
  ) external  returns (bool);

  function increaseAllowance(address spender, uint256 addedValue)
    external

    returns (bool);

  function decreaseAllowance(address spender, uint256 subtractedValue)
    external

    returns (bool);

  /**************************************************************************
   * ERC-777 functions
   *************************************************************************/

  function granularity() external pure  returns (uint256);

